#!/bin/bash
# Uninstall script for 010 Editor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling 010 Editor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping 010 Editor if running..."
pkill -f "010 Editor" 2>/dev/null || true

# Remove /Applications/010 Editor.app
echo "Removing /Applications/010 Editor.app..."
if [ -d "/Applications/010 Editor.app" ]; then
    rm -rf "/Applications/010 Editor.app" 2>/dev/null || true
elif [ -f "/Applications/010 Editor.app" ]; then
    rm -f "/Applications/010 Editor.app" 2>/dev/null || true
fi

# Remove $HOME/.config/SweetScape
echo "Removing $HOME/.config/SweetScape..."
if [ -d "$HOME/.config/SweetScape" ]; then
    rm -rf "$HOME/.config/SweetScape" 2>/dev/null || true
elif [ -f "$HOME/.config/SweetScape" ]; then
    rm -f "$HOME/.config/SweetScape" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/SweetScape
echo "Removing $HOME/Library/Application Support/SweetScape..."
if [ -d "$HOME/Library/Application Support/SweetScape" ]; then
    rm -rf "$HOME/Library/Application Support/SweetScape" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/SweetScape" ]; then
    rm -f "$HOME/Library/Application Support/SweetScape" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.SweetScape.010Editor.savedState
echo "Removing $HOME/Library/Saved Application State/com.SweetScape.010Editor.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.SweetScape.010Editor.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.SweetScape.010Editor.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.SweetScape.010Editor.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.SweetScape.010Editor.savedState" 2>/dev/null || true
fi

# Remove $HOME/Documents/SweetScape
echo "Removing $HOME/Documents/SweetScape..."
if [ -d "$HOME/Documents/SweetScape" ]; then
    rm -rf "$HOME/Documents/SweetScape" 2>/dev/null || true
elif [ -f "$HOME/Documents/SweetScape" ]; then
    rm -f "$HOME/Documents/SweetScape" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
