#!/bin/bash
# Uninstall script for 1Password CLI
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling 1Password CLI..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping 1Password CLI if running..."
pkill -f "1Password CLI" 2>/dev/null || true

# Remove /usr/local/bin/op
echo "Removing /usr/local/bin/op..."
if [ -d "/usr/local/bin/op" ]; then
    rm -rf "/usr/local/bin/op" 2>/dev/null || true
elif [ -f "/usr/local/bin/op" ]; then
    rm -f "/usr/local/bin/op" 2>/dev/null || true
fi

# Remove $HOME/.op
echo "Removing $HOME/.op..."
if [ -d "$HOME/.op" ]; then
    rm -rf "$HOME/.op" 2>/dev/null || true
elif [ -f "$HOME/.op" ]; then
    rm -f "$HOME/.op" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
