#!/bin/bash
# Uninstall script for 4K Slideshow Maker
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling 4K Slideshow Maker..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping 4K Slideshow Maker if running..."
pkill -f "4K Slideshow Maker" 2>/dev/null || true

# Remove /Applications/4K Slideshow Maker.app
echo "Removing /Applications/4K Slideshow Maker.app..."
if [ -d "/Applications/4K Slideshow Maker.app" ]; then
    rm -rf "/Applications/4K Slideshow Maker.app" 2>/dev/null || true
elif [ -f "/Applications/4K Slideshow Maker.app" ]; then
    rm -f "/Applications/4K Slideshow Maker.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/4kdownload.com
echo "Removing $HOME/Library/Application Support/4kdownload.com..."
if [ -d "$HOME/Library/Application Support/4kdownload.com" ]; then
    rm -rf "$HOME/Library/Application Support/4kdownload.com" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/4kdownload.com" ]; then
    rm -f "$HOME/Library/Application Support/4kdownload.com" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.4kdownload.4K Slideshow Maker.plist
echo "Removing $HOME/Library/Preferences/com.4kdownload.4K Slideshow Maker.plist..."
if [ -d "$HOME/Library/Preferences/com.4kdownload.4K Slideshow Maker.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.4kdownload.4K Slideshow Maker.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.4kdownload.4K Slideshow Maker.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.4kdownload.4K Slideshow Maker.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.openmedia.4kslideshowmaker.savedState
echo "Removing $HOME/Library/Saved Application State/com.openmedia.4kslideshowmaker.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.openmedia.4kslideshowmaker.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.openmedia.4kslideshowmaker.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.openmedia.4kslideshowmaker.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.openmedia.4kslideshowmaker.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
