#!/bin/bash
# Uninstall script for 4K Stogram
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling 4K Stogram..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping 4K Stogram if running..."
pkill -f "4K Stogram" 2>/dev/null || true

# Remove /Applications/4K Stogram.app
echo "Removing /Applications/4K Stogram.app..."
if [ -d "/Applications/4K Stogram.app" ]; then
    rm -rf "/Applications/4K Stogram.app" 2>/dev/null || true
elif [ -f "/Applications/4K Stogram.app" ]; then
    rm -f "/Applications/4K Stogram.app" 2>/dev/null || true
fi

# Remove $HOME/Pictures/4K Stogram
echo "Removing $HOME/Pictures/4K Stogram..."
if [ -d "$HOME/Pictures/4K Stogram" ]; then
    rm -rf "$HOME/Pictures/4K Stogram" 2>/dev/null || true
elif [ -f "$HOME/Pictures/4K Stogram" ]; then
    rm -f "$HOME/Pictures/4K Stogram" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
