#!/bin/bash
# Uninstall script for 4K Video to MP3
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling 4K Video to MP3..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping 4K Video to MP3 if running..."
pkill -f "4K Video to MP3" 2>/dev/null || true

# Remove /Applications/4K Video to MP3.app
echo "Removing /Applications/4K Video to MP3.app..."
if [ -d "/Applications/4K Video to MP3.app" ]; then
    rm -rf "/Applications/4K Video to MP3.app" 2>/dev/null || true
elif [ -f "/Applications/4K Video to MP3.app" ]; then
    rm -f "/Applications/4K Video to MP3.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/4kdownload.com
echo "Removing $HOME/Library/Application Support/4kdownload.com..."
if [ -d "$HOME/Library/Application Support/4kdownload.com" ]; then
    rm -rf "$HOME/Library/Application Support/4kdownload.com" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/4kdownload.com" ]; then
    rm -f "$HOME/Library/Application Support/4kdownload.com" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.4kdownload.4K Video to MP3.plist
echo "Removing $HOME/Library/Preferences/com.4kdownload.4K Video to MP3.plist..."
if [ -d "$HOME/Library/Preferences/com.4kdownload.4K Video to MP3.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.4kdownload.4K Video to MP3.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.4kdownload.4K Video to MP3.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.4kdownload.4K Video to MP3.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
