fi

# Remove $HOME/Library/Application Support/CrashReporter/Live_*.plist
echo "Removing $HOME/Library/Application Support/CrashReporter/Live_*.plist..."
if [ -d "$HOME/Library/Application Support/CrashReporter/Live_*.plist" ]; then
    rm -rf "$HOME/Library/Application Support/CrashReporter/Live_*.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/CrashReporter/Live_*.plist" ]; then
    rm -f "$HOME/Library/Application Support/CrashReporter/Live_*.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Ableton
echo "Removing $HOME/Library/Caches/Ableton..."
if [ -d "$HOME/Library/Caches/Ableton" ]; then
    rm -rf "$HOME/Library/Caches/Ableton" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Ableton" ]; then
    rm -f "$HOME/Library/Caches/Ableton" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Ableton
echo "Removing $HOME/Library/Preferences/Ableton..."
if [ -d "$HOME/Library/Preferences/Ableton" ]; then
    rm -rf "$HOME/Library/Preferences/Ableton" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Ableton" ]; then
    rm -f "$HOME/Library/Preferences/Ableton" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.ableton.live.plist*
echo "Removing $HOME/Library/Preferences/com.ableton.live.plist*..."
if [ -d "$HOME/Library/Preferences/com.ableton.live.plist*" ]; then
    rm -rf "$HOME/Library/Preferences/com.ableton.live.plist*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.ableton.live.plist*" ]; then
    rm -f "$HOME/Library/Preferences/com.ableton.live.plist*" 2>/dev/null || true
fi

# Remove $HOME/Music/Ableton
echo "Removing $HOME/Music/Ableton..."
