#!/bin/bash
# Uninstall script for ActivityWatch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ActivityWatch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ActivityWatch if running..."
pkill -f "ActivityWatch" 2>/dev/null || true

# Remove /Applications/ActivityWatch.app
echo "Removing /Applications/ActivityWatch.app..."
if [ -d "/Applications/ActivityWatch.app" ]; then
    rm -rf "/Applications/ActivityWatch.app" 2>/dev/null || true
elif [ -f "/Applications/ActivityWatch.app" ]; then
    rm -f "/Applications/ActivityWatch.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/activitywatch
echo "Removing $HOME/Library/Application Support/activitywatch..."
if [ -d "$HOME/Library/Application Support/activitywatch" ]; then
    rm -rf "$HOME/Library/Application Support/activitywatch" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/activitywatch" ]; then
    rm -f "$HOME/Library/Application Support/activitywatch" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/activitywatch
echo "Removing $HOME/Library/Caches/activitywatch..."
if [ -d "$HOME/Library/Caches/activitywatch" ]; then
    rm -rf "$HOME/Library/Caches/activitywatch" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/activitywatch" ]; then
    rm -f "$HOME/Library/Caches/activitywatch" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/activitywatch
echo "Removing $HOME/Library/Logs/activitywatch..."
if [ -d "$HOME/Library/Logs/activitywatch" ]; then
    rm -rf "$HOME/Library/Logs/activitywatch" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/activitywatch" ]; then
    rm -f "$HOME/Library/Logs/activitywatch" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
