echo "Removing /Applications/Adium.app..."
if [ -d "/Applications/Adium.app" ]; then
    rm -rf "/Applications/Adium.app" 2>/dev/null || true
elif [ -f "/Applications/Adium.app" ]; then
    rm -f "/Applications/Adium.app" 2>/dev/null || true
fi

