#!/bin/bash
# Uninstall script for Adobe Digital Editions
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Adobe Digital Editions..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Adobe Digital Editions if running..."
pkill -f "Adobe Digital Editions" 2>/dev/null || true

# Kill application with bundle ID com.adobe.adobedigitaleditions.app if running
echo "Stopping application with bundle ID com.adobe.adobedigitaleditions.app if running..."
killall -9 "com.adobe.adobedigitaleditions.app" 2>/dev/null || true

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.adobe.adobedigitaleditions.app.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.adobe.adobedigitaleditions.app.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.adobe.adobedigitaleditions.app.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.adobe.adobedigitaleditions.app.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.adobe.adobedigitaleditions.app.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.adobe.adobedigitaleditions.app.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.adobe.adobedigitaleditions.app
echo "Removing $HOME/Library/HTTPStorages/com.adobe.adobedigitaleditions.app..."
if [ -d "$HOME/Library/HTTPStorages/com.adobe.adobedigitaleditions.app" ]; then
    rm -rf "$HOME/Library/HTTPStorages/com.adobe.adobedigitaleditions.app" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/com.adobe.adobedigitaleditions.app" ]; then
    rm -f "$HOME/Library/HTTPStorages/com.adobe.adobedigitaleditions.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.adobe.adobedigitaleditions.app.plist
echo "Removing $HOME/Library/Preferences/com.adobe.adobedigitaleditions.app.plist..."
if [ -d "$HOME/Library/Preferences/com.adobe.adobedigitaleditions.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.adobe.adobedigitaleditions.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.adobe.adobedigitaleditions.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.adobe.adobedigitaleditions.app.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
