#!/bin/bash
# Uninstall script for Adobe DNG Converter
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Adobe DNG Converter..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Adobe DNG Converter if running..."
pkill -f "Adobe DNG Converter" 2>/dev/null || true

# Kill application with bundle ID com.adobe.DNGConverter if running
echo "Stopping application with bundle ID com.adobe.DNGConverter if running..."
killall -9 "com.adobe.DNGConverter" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Adobe/CameraRaw/GPU/Adobe DNG Converter
echo "Removing $HOME/Library/Application Support/Adobe/CameraRaw/GPU/Adobe DNG Converter..."
if [ -d "$HOME/Library/Application Support/Adobe/CameraRaw/GPU/Adobe DNG Converter" ]; then
    rm -rf "$HOME/Library/Application Support/Adobe/CameraRaw/GPU/Adobe DNG Converter" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Adobe/CameraRaw/GPU/Adobe DNG Converter" ]; then
    rm -f "$HOME/Library/Application Support/Adobe/CameraRaw/GPU/Adobe DNG Converter" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Adobe/CameraRaw/Logs/DNG Converter Log*
echo "Removing $HOME/Library/Application Support/Adobe/CameraRaw/Logs/DNG Converter Log*..."
if [ -d "$HOME/Library/Application Support/Adobe/CameraRaw/Logs/DNG Converter Log*" ]; then
    rm -rf "$HOME/Library/Application Support/Adobe/CameraRaw/Logs/DNG Converter Log*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Adobe/CameraRaw/Logs/DNG Converter Log*" ]; then
    rm -f "$HOME/Library/Application Support/Adobe/CameraRaw/Logs/DNG Converter Log*" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.adobe.DNGConverter.savedState
echo "Removing $HOME/Library/Saved Application State/com.adobe.DNGConverter.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.adobe.DNGConverter.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.adobe.DNGConverter.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.adobe.DNGConverter.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.adobe.DNGConverter.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
