if [ -d "/Applications/AirDroid.app" ]; then
    rm -rf "/Applications/AirDroid.app" 2>/dev/null || true
elif [ -f "/Applications/AirDroid.app" ]; then
    rm -f "/Applications/AirDroid.app" 2>/dev/null || true
fi

