#!/bin/bash
# Uninstall script for AirServer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling AirServer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping AirServer if running..."
pkill -f "AirServer" 2>/dev/null || true

# Remove /Applications/AirServer.app
echo "Removing /Applications/AirServer.app..."
if [ -d "/Applications/AirServer.app" ]; then
    rm -rf "/Applications/AirServer.app" 2>/dev/null || true
elif [ -f "/Applications/AirServer.app" ]; then
    rm -f "/Applications/AirServer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.pratikkumar.airserver-mac
echo "Removing $HOME/Library/Caches/com.pratikkumar.airserver-mac..."
if [ -d "$HOME/Library/Caches/com.pratikkumar.airserver-mac" ]; then
    rm -rf "$HOME/Library/Caches/com.pratikkumar.airserver-mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.pratikkumar.airserver-mac" ]; then
    rm -f "$HOME/Library/Caches/com.pratikkumar.airserver-mac" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.pratikkumar.airserver-mac.AirServer.plist
echo "Removing $HOME/Library/Preferences/com.pratikkumar.airserver-mac.AirServer.plist..."
if [ -d "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.AirServer.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.AirServer.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.AirServer.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.AirServer.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.pratikkumar.airserver-mac.plist
echo "Removing $HOME/Library/Preferences/com.pratikkumar.airserver-mac.plist..."
if [ -d "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.pratikkumar.airserver-mac.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
