#!/bin/bash
# Uninstall script for Airtame
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Airtame..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Airtame if running..."
pkill -f "Airtame" 2>/dev/null || true

# Remove /Applications/Airtame.app
echo "Removing /Applications/Airtame.app..."
if [ -d "/Applications/Airtame.app" ]; then
    rm -rf "/Applications/Airtame.app" 2>/dev/null || true
elif [ -f "/Applications/Airtame.app" ]; then
    rm -f "/Applications/Airtame.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/airtame-application
echo "Removing $HOME/Library/Application Support/airtame-application..."
if [ -d "$HOME/Library/Application Support/airtame-application" ]; then
    rm -rf "$HOME/Library/Application Support/airtame-application" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/airtame-application" ]; then
    rm -f "$HOME/Library/Application Support/airtame-application" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Airtame
echo "Removing $HOME/Library/Logs/Airtame..."
if [ -d "$HOME/Library/Logs/Airtame" ]; then
    rm -rf "$HOME/Library/Logs/Airtame" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Airtame" ]; then
    rm -f "$HOME/Library/Logs/Airtame" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.airtame.airtame-application.plist
echo "Removing $HOME/Library/Preferences/com.airtame.airtame-application.plist..."
if [ -d "$HOME/Library/Preferences/com.airtame.airtame-application.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.airtame.airtame-application.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.airtame.airtame-application.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.airtame.airtame-application.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.airtame.airtame-application.savedState
echo "Removing $HOME/Library/Saved Application State/com.airtame.airtame-application.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.airtame.airtame-application.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.airtame.airtame-application.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.airtame.airtame-application.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.airtame.airtame-application.savedState" 2>/dev/null || true
