#!/bin/bash
# Uninstall script for Alloy
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Alloy..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Alloy if running..."
pkill -f "Alloy" 2>/dev/null || true

# Remove /Applications/Alloy.app
echo "Removing /Applications/Alloy.app..."
if [ -d "/Applications/Alloy.app" ]; then
    rm -rf "/Applications/Alloy.app" 2>/dev/null || true
elif [ -f "/Applications/Alloy.app" ]; then
    rm -f "/Applications/Alloy.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.alloytools.alloy.savedState
echo "Removing $HOME/Library/Saved Application State/org.alloytools.alloy.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.alloytools.alloy.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.alloytools.alloy.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.alloytools.alloy.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.alloytools.alloy.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
