#!/bin/bash
# Uninstall script for Altair GraphQL Client
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Altair GraphQL Client..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Altair GraphQL Client if running..."
pkill -f "Altair GraphQL Client" 2>/dev/null || true

# Remove /Applications/Altair GraphQL Client.app
echo "Removing /Applications/Altair GraphQL Client.app..."
if [ -d "/Applications/Altair GraphQL Client.app" ]; then
    rm -rf "/Applications/Altair GraphQL Client.app" 2>/dev/null || true
elif [ -f "/Applications/Altair GraphQL Client.app" ]; then
    rm -f "/Applications/Altair GraphQL Client.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/altair
