#!/bin/bash
# Uninstall script for Amie
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Amie..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Amie if running..."
pkill -f "Amie" 2>/dev/null || true

# Remove /Applications/Amie.app
echo "Removing /Applications/Amie.app..."
if [ -d "/Applications/Amie.app" ]; then
    rm -rf "/Applications/Amie.app" 2>/dev/null || true
elif [ -f "/Applications/Amie.app" ]; then
    rm -f "/Applications/Amie.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/amie-desktop
echo "Removing $HOME/Library/Application Support/amie-desktop..."
if [ -d "$HOME/Library/Application Support/amie-desktop" ]; then
    rm -rf "$HOME/Library/Application Support/amie-desktop" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/amie-desktop" ]; then
    rm -f "$HOME/Library/Application Support/amie-desktop" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/amie-desktop
echo "Removing $HOME/Library/Caches/amie-desktop..."
if [ -d "$HOME/Library/Caches/amie-desktop" ]; then
    rm -rf "$HOME/Library/Caches/amie-desktop" 2>/dev/null || true
