echo "Removing $HOME/Library/Preferences/so.amie.electron-app.plist..."
if [ -d "$HOME/Library/Preferences/so.amie.electron-app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/so.amie.electron-app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/so.amie.electron-app.plist" ]; then
    rm -f "$HOME/Library/Preferences/so.amie.electron-app.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/so.amie.electron-app.savedState
echo "Removing $HOME/Library/Saved Application State/so.amie.electron-app.savedState..."
if [ -d "$HOME/Library/Saved Application State/so.amie.electron-app.savedState" ]; then
