#!/bin/bash
# Uninstall script for Android File Transfer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Android File Transfer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Android File Transfer if running..."
pkill -f "Android File Transfer" 2>/dev/null || true

# Remove /Applications/Android File Transfer.app
echo "Removing /Applications/Android File Transfer.app..."
if [ -d "/Applications/Android File Transfer.app" ]; then
    rm -rf "/Applications/Android File Transfer.app" 2>/dev/null || true
elif [ -f "/Applications/Android File Transfer.app" ]; then
    rm -f "/Applications/Android File Transfer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.android.mtpviewer.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.android.mtpviewer.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.android.mtpviewer.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.android.mtpviewer.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.android.mtpviewer.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.google.android.mtpviewer.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Google/Android File Transfer
echo "Removing $HOME/Library/Application Support/Google/Android File Transfer..."
if [ -d "$HOME/Library/Application Support/Google/Android File Transfer" ]; then
    rm -rf "$HOME/Library/Application Support/Google/Android File Transfer" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Google/Android File Transfer" ]; then
    rm -f "$HOME/Library/Application Support/Google/Android File Transfer" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.google.android.mtpviewer.plist
echo "Removing $HOME/Library/Preferences/com.google.android.mtpviewer.plist..."
if [ -d "$HOME/Library/Preferences/com.google.android.mtpviewer.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.google.android.mtpviewer.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.google.android.mtpviewer.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.google.android.mtpviewer.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Google
echo "Removing $HOME/Library/Application Support/Google..."
if [ -d "$HOME/Library/Application Support/Google" ]; then
    rm -rf "$HOME/Library/Application Support/Google" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Google" ]; then
    rm -f "$HOME/Library/Application Support/Google" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
