#!/bin/bash
# Uninstall script for Android Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Android Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Android Studio if running..."
pkill -f "Android Studio" 2>/dev/null || true

# Remove /Applications/Android Studio.app
echo "Removing /Applications/Android Studio.app..."
if [ -d "/Applications/Android Studio.app" ]; then
    rm -rf "/Applications/Android Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Android Studio.app" ]; then
    rm -f "/Applications/Android Studio.app" 2>/dev/null || true
fi

# Remove binary /Applications/Android Studio.app/Android Studio.app/Contents/MacOS/studio
echo "Removing binary /Applications/Android Studio.app/Android Studio.app/Contents/MacOS/studio..."
if [ -f "/Applications/Android Studio.app/Android Studio.app/Contents/MacOS/studio" ]; then
    rm -f "/Applications/Android Studio.app/Android Studio.app/Contents/MacOS/studio" 2>/dev/null || true
fi

# Remove $HOME/.android
echo "Removing $HOME/.android..."
if [ -d "$HOME/.android" ]; then
    rm -rf "$HOME/.android" 2>/dev/null || true
elif [ -f "$HOME/.android" ]; then
    rm -f "$HOME/.android" 2>/dev/null || true
fi

# Remove $HOME/Library/Android/sdk
echo "Removing $HOME/Library/Android/sdk..."
if [ -d "$HOME/Library/Android/sdk" ]; then
    rm -rf "$HOME/Library/Android/sdk" 2>/dev/null || true
elif [ -f "$HOME/Library/Android/sdk" ]; then
    rm -f "$HOME/Library/Android/sdk" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Google/AndroidStudio2025.2
echo "Removing $HOME/Library/Application Support/Google/AndroidStudio2025.2..."
if [ -d "$HOME/Library/Application Support/Google/AndroidStudio2025.2" ]; then
    rm -rf "$HOME/Library/Application Support/Google/AndroidStudio2025.2" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Google/AndroidStudio2025.2" ]; then
    rm -f "$HOME/Library/Application Support/Google/AndroidStudio2025.2" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Google/AndroidStudio2025.2
echo "Removing $HOME/Library/Caches/Google/AndroidStudio2025.2..."
if [ -d "$HOME/Library/Caches/Google/AndroidStudio2025.2" ]; then
    rm -rf "$HOME/Library/Caches/Google/AndroidStudio2025.2" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Google/AndroidStudio2025.2" ]; then
    rm -f "$HOME/Library/Caches/Google/AndroidStudio2025.2" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Google/AndroidStudio2025.2
echo "Removing $HOME/Library/Logs/Google/AndroidStudio2025.2..."
if [ -d "$HOME/Library/Logs/Google/AndroidStudio2025.2" ]; then
    rm -rf "$HOME/Library/Logs/Google/AndroidStudio2025.2" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Google/AndroidStudio2025.2" ]; then
    rm -f "$HOME/Library/Logs/Google/AndroidStudio2025.2" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.android.Emulator.plist
echo "Removing $HOME/Library/Preferences/com.android.Emulator.plist..."
if [ -d "$HOME/Library/Preferences/com.android.Emulator.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.android.Emulator.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.android.Emulator.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.android.Emulator.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.google.android.studio.plist
echo "Removing $HOME/Library/Preferences/com.google.android.studio.plist..."
if [ -d "$HOME/Library/Preferences/com.google.android.studio.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.google.android.studio.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.google.android.studio.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.google.android.studio.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.google.android.studio.savedState
echo "Removing $HOME/Library/Saved Application State/com.google.android.studio.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.google.android.studio.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.google.android.studio.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.google.android.studio.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.google.android.studio.savedState" 2>/dev/null || true
fi

# Remove $HOME/AndroidStudioProjects
echo "Removing $HOME/AndroidStudioProjects..."
if [ -d "$HOME/AndroidStudioProjects" ]; then
    rm -rf "$HOME/AndroidStudioProjects" 2>/dev/null || true
elif [ -f "$HOME/AndroidStudioProjects" ]; then
    rm -f "$HOME/AndroidStudioProjects" 2>/dev/null || true
fi

# Remove $HOME/Library/Android
echo "Removing $HOME/Library/Android..."
if [ -d "$HOME/Library/Android" ]; then
    rm -rf "$HOME/Library/Android" 2>/dev/null || true
