#!/bin/bash
# Uninstall script for AppGrid
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling AppGrid..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping AppGrid if running..."
pkill -f "AppGrid" 2>/dev/null || true

# Kill application with bundle ID com.sdegutis.AppGrid if running
echo "Stopping application with bundle ID com.sdegutis.AppGrid if running..."
killall -9 "com.sdegutis.AppGrid" 2>/dev/null || true

# Remove /Applications/AppGrid.app
echo "Removing /Applications/AppGrid.app..."
if [ -d "/Applications/AppGrid.app" ]; then
    rm -rf "/Applications/AppGrid.app" 2>/dev/null || true
elif [ -f "/Applications/AppGrid.app" ]; then
    rm -f "/Applications/AppGrid.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.sdegutis.AppGrid.plist
echo "Removing $HOME/Library/Preferences/com.sdegutis.AppGrid.plist..."
if [ -d "$HOME/Library/Preferences/com.sdegutis.AppGrid.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.sdegutis.AppGrid.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.sdegutis.AppGrid.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.sdegutis.AppGrid.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
