#!/bin/bash
# Uninstall script for Apptivate
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Apptivate..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Apptivate if running..."
pkill -f "Apptivate" 2>/dev/null || true

# Remove /Applications/Apptivate.app
echo "Removing /Applications/Apptivate.app..."
if [ -d "/Applications/Apptivate.app" ]; then
    rm -rf "/Applications/Apptivate.app" 2>/dev/null || true
elif [ -f "/Applications/Apptivate.app" ]; then
    rm -f "/Applications/Apptivate.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Apptivate
echo "Removing $HOME/Library/Application Support/Apptivate..."
if [ -d "$HOME/Library/Application Support/Apptivate" ]; then
    rm -rf "$HOME/Library/Application Support/Apptivate" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Apptivate" ]; then
    rm -f "$HOME/Library/Application Support/Apptivate" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/se.cocoabeans.apptivate.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/se.cocoabeans.apptivate.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/se.cocoabeans.apptivate.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/se.cocoabeans.apptivate.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/se.cocoabeans.apptivate.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/se.cocoabeans.apptivate.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/se.cocoabeans.apptivate.plist
echo "Removing $HOME/Library/Preferences/se.cocoabeans.apptivate.plist..."
if [ -d "$HOME/Library/Preferences/se.cocoabeans.apptivate.plist" ]; then
    rm -rf "$HOME/Library/Preferences/se.cocoabeans.apptivate.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/se.cocoabeans.apptivate.plist" ]; then
    rm -f "$HOME/Library/Preferences/se.cocoabeans.apptivate.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
