#!/bin/bash
# Uninstall script for Aurora HDR
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Aurora HDR..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Aurora HDR if running..."
pkill -f "Aurora HDR" 2>/dev/null || true

# Remove /Applications/Aurora HDR.app
echo "Removing /Applications/Aurora HDR.app..."
if [ -d "/Applications/Aurora HDR.app" ]; then
    rm -rf "/Applications/Aurora HDR.app" 2>/dev/null || true
elif [ -f "/Applications/Aurora HDR.app" ]; then
    rm -f "/Applications/Aurora HDR.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.macphun.auroraHDR2019
echo "Removing $HOME/Library/Caches/com.macphun.auroraHDR2019..."
if [ -d "$HOME/Library/Caches/com.macphun.auroraHDR2019" ]; then
    rm -rf "$HOME/Library/Caches/com.macphun.auroraHDR2019" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.macphun.auroraHDR2019" ]; then
    rm -f "$HOME/Library/Caches/com.macphun.auroraHDR2019" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.macphun.auroraHDR2019.plist
echo "Removing $HOME/Library/Preferences/com.macphun.auroraHDR2019.plist..."
if [ -d "$HOME/Library/Preferences/com.macphun.auroraHDR2019.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.macphun.auroraHDR2019.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.macphun.auroraHDR2019.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.macphun.auroraHDR2019.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
