#!/bin/bash
# Uninstall script for Avast Secure Browser
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Avast Secure Browser..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
