    rm -rf "/Applications/Avast Secure Browser.app" 2>/dev/null || true
elif [ -f "/Applications/Avast Secure Browser.app" ]; then
    rm -f "/Applications/Avast Secure Browser.app" 2>/dev/null || true
fi

