# Remove /Applications/Axure RP 11.app
echo "Removing /Applications/Axure RP 11.app..."
if [ -d "/Applications/Axure RP 11.app" ]; then
    rm -rf "/Applications/Axure RP 11.app" 2>/dev/null || true
elif [ -f "/Applications/Axure RP 11.app" ]; then
    rm -f "/Applications/Axure RP 11.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.axure.axurerp#*.sfl*
