elif [ -f "$HOME/Library/Preferences/com.axure.AxureRP#*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.axure.AxureRP#*.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.axure.AxureRP#*.savedState
echo "Removing $HOME/Library/Saved Application State/com.axure.AxureRP#*.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.axure.AxureRP#*.savedState" ]; then
