#!/bin/bash
# Uninstall script for Backblaze
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Backblaze..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Backblaze if running..."
pkill -f "Backblaze" 2>/dev/null || true

# Unload service com.backblaze.bzbmenu
echo "Unloading service com.backblaze.bzbmenu..."
launchctl unload -w /Library/LaunchAgents/com.backblaze.bzbmenu.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.backblaze.bzbmenu.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.backblaze.bzbmenu.plist 2>/dev/null || true

# Unload service com.backblaze.bzserv
echo "Unloading service com.backblaze.bzserv..."
launchctl unload -w /Library/LaunchAgents/com.backblaze.bzserv.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.backblaze.bzserv.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.backblaze.bzserv.plist 2>/dev/null || true

# Remove /Library/Backblaze.bzpkg
echo "Removing /Library/Backblaze.bzpkg..."
if [ -d "/Library/Backblaze.bzpkg" ]; then
    rm -rf "/Library/Backblaze.bzpkg" 2>/dev/null || true
elif [ -f "/Library/Backblaze.bzpkg" ]; then
    rm -f "/Library/Backblaze.bzpkg" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.backblaze.*.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.backblaze.*.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.backblaze.*.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.backblaze.*.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.backblaze.*.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.backblaze.*.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/BackblazeGUIInstaller
echo "Removing $HOME/Library/Logs/BackblazeGUIInstaller..."
if [ -d "$HOME/Library/Logs/BackblazeGUIInstaller" ]; then
    rm -rf "$HOME/Library/Logs/BackblazeGUIInstaller" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/BackblazeGUIInstaller" ]; then
    rm -f "$HOME/Library/Logs/BackblazeGUIInstaller" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.backblaze.bzbmenu.plist
echo "Removing $HOME/Library/Preferences/com.backblaze.bzbmenu.plist..."
if [ -d "$HOME/Library/Preferences/com.backblaze.bzbmenu.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.backblaze.bzbmenu.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.backblaze.bzbmenu.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.backblaze.bzbmenu.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
