#!/bin/bash
# Uninstall script for BasicTeX
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BasicTeX..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BasicTeX if running..."
pkill -f "BasicTeX" 2>/dev/null || true

# Remove /usr/local/texlive/texmf-local
echo "Removing /usr/local/texlive/texmf-local..."
if [ -d "/usr/local/texlive/texmf-local" ]; then
    rm -rf "/usr/local/texlive/texmf-local" 2>/dev/null || true
elif [ -f "/usr/local/texlive/texmf-local" ]; then
    rm -f "/usr/local/texlive/texmf-local" 2>/dev/null || true
fi

# Remove $HOME/Library/texlive/2025basic
echo "Removing $HOME/Library/texlive/2025basic..."
if [ -d "$HOME/Library/texlive/2025basic" ]; then
    rm -rf "$HOME/Library/texlive/2025basic" 2>/dev/null || true
elif [ -f "$HOME/Library/texlive/2025basic" ]; then
    rm -f "$HOME/Library/texlive/2025basic" 2>/dev/null || true
fi

# Remove /usr/local/texlive
echo "Removing /usr/local/texlive..."
if [ -d "/usr/local/texlive" ]; then
    rm -rf "/usr/local/texlive" 2>/dev/null || true
elif [ -f "/usr/local/texlive" ]; then
    rm -f "/usr/local/texlive" 2>/dev/null || true
fi

# Remove $HOME/Library/texlive
echo "Removing $HOME/Library/texlive..."
if [ -d "$HOME/Library/texlive" ]; then
    rm -rf "$HOME/Library/texlive" 2>/dev/null || true
elif [ -f "$HOME/Library/texlive" ]; then
    rm -f "$HOME/Library/texlive" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
