#!/bin/bash
# Uninstall script for Bdash
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Bdash..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Bdash if running..."
pkill -f "Bdash" 2>/dev/null || true

# Remove /Applications/Bdash.app
echo "Removing /Applications/Bdash.app..."
if [ -d "/Applications/Bdash.app" ]; then
    rm -rf "/Applications/Bdash.app" 2>/dev/null || true
elif [ -f "/Applications/Bdash.app" ]; then
    rm -f "/Applications/Bdash.app" 2>/dev/null || true
fi

# Remove $HOME/.bdash
echo "Removing $HOME/.bdash..."
if [ -d "$HOME/.bdash" ]; then
    rm -rf "$HOME/.bdash" 2>/dev/null || true
elif [ -f "$HOME/.bdash" ]; then
    rm -f "$HOME/.bdash" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Bdash
echo "Removing $HOME/Library/Application Support/Bdash..."
if [ -d "$HOME/Library/Application Support/Bdash" ]; then
    rm -rf "$HOME/Library/Application Support/Bdash" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Bdash" ]; then
    rm -f "$HOME/Library/Application Support/Bdash" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Bdash
echo "Removing $HOME/Library/Logs/Bdash..."
if [ -d "$HOME/Library/Logs/Bdash" ]; then
    rm -rf "$HOME/Library/Logs/Bdash" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Bdash" ]; then
    rm -f "$HOME/Library/Logs/Bdash" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/io.bdash.plist
echo "Removing $HOME/Library/Preferences/io.bdash.plist..."
if [ -d "$HOME/Library/Preferences/io.bdash.plist" ]; then
    rm -rf "$HOME/Library/Preferences/io.bdash.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/io.bdash.plist" ]; then
    rm -f "$HOME/Library/Preferences/io.bdash.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/io.bdash.savedState
echo "Removing $HOME/Library/Saved Application State/io.bdash.savedState..."
if [ -d "$HOME/Library/Saved Application State/io.bdash.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/io.bdash.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/io.bdash.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/io.bdash.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
