#!/bin/bash
# Uninstall script for BeardedSpice
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BeardedSpice..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BeardedSpice if running..."
pkill -f "BeardedSpice" 2>/dev/null || true

# Remove /Applications/BeardedSpice.app
echo "Removing /Applications/BeardedSpice.app..."
if [ -d "/Applications/BeardedSpice.app" ]; then
    rm -rf "/Applications/BeardedSpice.app" 2>/dev/null || true
elif [ -f "/Applications/BeardedSpice.app" ]; then
    rm -f "/Applications/BeardedSpice.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.beardedspice.BeardedSpice
echo "Removing $HOME/Library/Caches/com.beardedspice.BeardedSpice..."
if [ -d "$HOME/Library/Caches/com.beardedspice.BeardedSpice" ]; then
    rm -rf "$HOME/Library/Caches/com.beardedspice.BeardedSpice" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.beardedspice.BeardedSpice" ]; then
    rm -f "$HOME/Library/Caches/com.beardedspice.BeardedSpice" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.beardedspice.BeardedSpice.plist
echo "Removing $HOME/Library/Preferences/com.beardedspice.BeardedSpice.plist..."
if [ -d "$HOME/Library/Preferences/com.beardedspice.BeardedSpice.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.beardedspice.BeardedSpice.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.beardedspice.BeardedSpice.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.beardedspice.BeardedSpice.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
