#!/bin/bash
# Uninstall script for Beekeeper Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Beekeeper Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Beekeeper Studio if running..."
pkill -f "Beekeeper Studio" 2>/dev/null || true

# Remove /Applications/Beekeeper Studio.app
echo "Removing /Applications/Beekeeper Studio.app..."
if [ -d "/Applications/Beekeeper Studio.app" ]; then
    rm -rf "/Applications/Beekeeper Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Beekeeper Studio.app" ]; then
    rm -f "/Applications/Beekeeper Studio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/beekeeper-studio
echo "Removing $HOME/Library/Application Support/beekeeper-studio..."
if [ -d "$HOME/Library/Application Support/beekeeper-studio" ]; then
    rm -rf "$HOME/Library/Application Support/beekeeper-studio" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/beekeeper-studio" ]; then
    rm -f "$HOME/Library/Application Support/beekeeper-studio" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Caches/beekeeper-studio-updater
echo "Removing $HOME/Library/Application Support/Caches/beekeeper-studio-updater..."
if [ -d "$HOME/Library/Application Support/Caches/beekeeper-studio-updater" ]; then
    rm -rf "$HOME/Library/Application Support/Caches/beekeeper-studio-updater" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Caches/beekeeper-studio-updater" ]; then
