fi

# Remove $HOME/Library/Caches/io.beekeeperstudio.desktop.ShipIt
echo "Removing $HOME/Library/Caches/io.beekeeperstudio.desktop.ShipIt..."
if [ -d "$HOME/Library/Caches/io.beekeeperstudio.desktop.ShipIt" ]; then
