    rm -f "$HOME/Library/Preferences/ByHost/io.beekeeperstudio.desktop.ShipIt.*.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/io.beekeeperstudio.desktop.plist
echo "Removing $HOME/Library/Preferences/io.beekeeperstudio.desktop.plist..."
if [ -d "$HOME/Library/Preferences/io.beekeeperstudio.desktop.plist" ]; then
