#!/bin/bash
# Uninstall script for Bilibili
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Bilibili..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Bilibili if running..."
pkill -f "Bilibili" 2>/dev/null || true

# Remove /Applications/哔哩哔哩.app
echo "Removing /Applications/哔哩哔哩.app..."
if [ -d "/Applications/哔哩哔哩.app" ]; then
    rm -rf "/Applications/哔哩哔哩.app" 2>/dev/null || true
elif [ -f "/Applications/哔哩哔哩.app" ]; then
    rm -f "/Applications/哔哩哔哩.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/bilibili
echo "Removing $HOME/Library/Application Support/bilibili..."
if [ -d "$HOME/Library/Application Support/bilibili" ]; then
    rm -rf "$HOME/Library/Application Support/bilibili" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/bilibili" ]; then
    rm -f "$HOME/Library/Application Support/bilibili" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/bilibili
echo "Removing $HOME/Library/Logs/bilibili..."
if [ -d "$HOME/Library/Logs/bilibili" ]; then
    rm -rf "$HOME/Library/Logs/bilibili" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/bilibili" ]; then
    rm -f "$HOME/Library/Logs/bilibili" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.bilibili.bilibiliPC.plist
echo "Removing $HOME/Library/Preferences/com.bilibili.bilibiliPC.plist..."
if [ -d "$HOME/Library/Preferences/com.bilibili.bilibiliPC.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.bilibili.bilibiliPC.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.bilibili.bilibiliPC.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.bilibili.bilibiliPC.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.bilibili.bilibiliPC.savedState
echo "Removing $HOME/Library/Saved Application State/com.bilibili.bilibiliPC.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.bilibili.bilibiliPC.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.bilibili.bilibiliPC.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.bilibili.bilibiliPC.savedState" ]; then
