#!/bin/bash
# Uninstall script for Binance
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Binance..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Binance if running..."
pkill -f "Binance" 2>/dev/null || true

# Remove /Applications/Binance.app
echo "Removing /Applications/Binance.app..."
if [ -d "/Applications/Binance.app" ]; then
    rm -rf "/Applications/Binance.app" 2>/dev/null || true
elif [ -f "/Applications/Binance.app" ]; then
    rm -f "/Applications/Binance.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Binance
echo "Removing $HOME/Library/Application Support/Binance..."
if [ -d "$HOME/Library/Application Support/Binance" ]; then
    rm -rf "$HOME/Library/Application Support/Binance" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Binance" ]; then
    rm -f "$HOME/Library/Application Support/Binance" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Binance
echo "Removing $HOME/Library/Logs/Binance..."
if [ -d "$HOME/Library/Logs/Binance" ]; then
    rm -rf "$HOME/Library/Logs/Binance" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Binance" ]; then
    rm -f "$HOME/Library/Logs/Binance" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.binance.BinanceDesktop.savedState
echo "Removing $HOME/Library/Saved Application State/com.binance.BinanceDesktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.binance.BinanceDesktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.binance.BinanceDesktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.binance.BinanceDesktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.binance.BinanceDesktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
