#!/bin/bash
# Uninstall script for BirdFont
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BirdFont..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BirdFont if running..."
pkill -f "BirdFont" 2>/dev/null || true

# Remove /Applications/BirdFontNonCommercial.app
echo "Removing /Applications/BirdFontNonCommercial.app..."
if [ -d "/Applications/BirdFontNonCommercial.app" ]; then
    rm -rf "/Applications/BirdFontNonCommercial.app" 2>/dev/null || true
elif [ -f "/Applications/BirdFontNonCommercial.app" ]; then
    rm -f "/Applications/BirdFontNonCommercial.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/org.birdfont.birdfont
echo "Removing $HOME/Library/Application Scripts/org.birdfont.birdfont..."
if [ -d "$HOME/Library/Application Scripts/org.birdfont.birdfont" ]; then
    rm -rf "$HOME/Library/Application Scripts/org.birdfont.birdfont" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/org.birdfont.birdfont" ]; then
    rm -f "$HOME/Library/Application Scripts/org.birdfont.birdfont" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/org.birdfont.birdfont
echo "Removing $HOME/Library/Containers/org.birdfont.birdfont..."
if [ -d "$HOME/Library/Containers/org.birdfont.birdfont" ]; then
    rm -rf "$HOME/Library/Containers/org.birdfont.birdfont" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/org.birdfont.birdfont" ]; then
    rm -f "$HOME/Library/Containers/org.birdfont.birdfont" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
