#!/bin/bash
# Uninstall script for BitBar
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BitBar..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BitBar if running..."
pkill -f "BitBar" 2>/dev/null || true

# Remove /Applications/BitBar.app
echo "Removing /Applications/BitBar.app..."
if [ -d "/Applications/BitBar.app" ]; then
    rm -rf "/Applications/BitBar.app" 2>/dev/null || true
elif [ -f "/Applications/BitBar.app" ]; then
    rm -f "/Applications/BitBar.app" 2>/dev/null || true
fi

# Remove $HOME/Library/BitBar Plugins
echo "Removing $HOME/Library/BitBar Plugins..."
if [ -d "$HOME/Library/BitBar Plugins" ]; then
    rm -rf "$HOME/Library/BitBar Plugins" 2>/dev/null || true
elif [ -f "$HOME/Library/BitBar Plugins" ]; then
    rm -f "$HOME/Library/BitBar Plugins" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.matryer.BitBar
echo "Removing $HOME/Library/Caches/com.matryer.BitBar..."
if [ -d "$HOME/Library/Caches/com.matryer.BitBar" ]; then
    rm -rf "$HOME/Library/Caches/com.matryer.BitBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.matryer.BitBar" ]; then
    rm -f "$HOME/Library/Caches/com.matryer.BitBar" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.matryer.BitBar.plist
echo "Removing $HOME/Library/Preferences/com.matryer.BitBar.plist..."
if [ -d "$HOME/Library/Preferences/com.matryer.BitBar.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.matryer.BitBar.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.matryer.BitBar.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.matryer.BitBar.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
