#!/bin/bash
# Uninstall script for BitBox
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BitBox..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BitBox if running..."
pkill -f "BitBox" 2>/dev/null || true

# Remove /Applications/BitBox.app
echo "Removing /Applications/BitBox.app..."
if [ -d "/Applications/BitBox.app" ]; then
    rm -rf "/Applications/BitBox.app" 2>/dev/null || true
elif [ -f "/Applications/BitBox.app" ]; then
    rm -f "/Applications/BitBox.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/ch.shiftcrypto.BitBoxApp.plist
echo "Removing $HOME/Library/Preferences/ch.shiftcrypto.BitBoxApp.plist..."
if [ -d "$HOME/Library/Preferences/ch.shiftcrypto.BitBoxApp.plist" ]; then
    rm -rf "$HOME/Library/Preferences/ch.shiftcrypto.BitBoxApp.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/ch.shiftcrypto.BitBoxApp.plist" ]; then
    rm -f "$HOME/Library/Preferences/ch.shiftcrypto.BitBoxApp.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/ch.shiftcrypto.wallet.savedState
echo "Removing $HOME/Library/Saved Application State/ch.shiftcrypto.wallet.savedState..."
if [ -d "$HOME/Library/Saved Application State/ch.shiftcrypto.wallet.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/ch.shiftcrypto.wallet.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/ch.shiftcrypto.wallet.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/ch.shiftcrypto.wallet.savedState" 2>/dev/null || true
