#!/bin/bash
# Uninstall script for BlackHole 2ch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BlackHole 2ch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BlackHole 2ch if running..."
pkill -f "BlackHole 2ch" 2>/dev/null || true

# Kill application with bundle ID com.apple.audio.AudioMIDISetup if running
echo "Stopping application with bundle ID com.apple.audio.AudioMIDISetup if running..."
killall -9 "com.apple.audio.AudioMIDISetup" 2>/dev/null || true

echo "Uninstallation complete!"
exit 0
