#!/bin/bash
# Uninstall script for BlueJ
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BlueJ..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BlueJ if running..."
pkill -f "BlueJ" 2>/dev/null || true

# Remove /Applications/BlueJ.app
echo "Removing /Applications/BlueJ.app..."
if [ -d "/Applications/BlueJ.app" ]; then
    rm -rf "/Applications/BlueJ.app" 2>/dev/null || true
elif [ -f "/Applications/BlueJ.app" ]; then
    rm -f "/Applications/BlueJ.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.bluej
echo "Removing $HOME/Library/Preferences/org.bluej..."
if [ -d "$HOME/Library/Preferences/org.bluej" ]; then
    rm -rf "$HOME/Library/Preferences/org.bluej" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.bluej" ]; then
    rm -f "$HOME/Library/Preferences/org.bluej" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
