#!/bin/bash
# Uninstall script for Box Drive
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Box Drive..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Box Drive if running..."
pkill -f "Box Drive" 2>/dev/null || true

# Unload service com.box.desktop.helper
echo "Unloading service com.box.desktop.helper..."
launchctl unload -w /Library/LaunchAgents/com.box.desktop.helper.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.box.desktop.helper.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.box.desktop.helper.plist 2>/dev/null || true

# Kill application with bundle ID com.box.Box-Local-Com-Server if running
echo "Stopping application with bundle ID com.box.Box-Local-Com-Server if running..."
killall -9 "com.box.Box-Local-Com-Server" 2>/dev/null || true

# Kill application with bundle ID com.box.desktop if running
echo "Stopping application with bundle ID com.box.desktop if running..."
killall -9 "com.box.desktop" 2>/dev/null || true

# Kill application with bundle ID com.box.desktop.findersyncext if running
echo "Stopping application with bundle ID com.box.desktop.findersyncext if running..."
killall -9 "com.box.desktop.findersyncext" 2>/dev/null || true

# Kill application with bundle ID com.box.desktop.helper if running
echo "Stopping application with bundle ID com.box.desktop.helper if running..."
killall -9 "com.box.desktop.helper" 2>/dev/null || true

# Kill application with bundle ID com.box.desktop.ui if running
echo "Stopping application with bundle ID com.box.desktop.ui if running..."
killall -9 "com.box.desktop.ui" 2>/dev/null || true

# Remove $HOME/.Box_*
echo "Removing $HOME/.Box_*..."
if [ -d "$HOME/.Box_*" ]; then
    rm -rf "$HOME/.Box_*" 2>/dev/null || true
elif [ -f "$HOME/.Box_*" ]; then
    rm -f "$HOME/.Box_*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Box/Box
echo "Removing $HOME/Library/Application Support/Box/Box..."
if [ -d "$HOME/Library/Application Support/Box/Box" ]; then
    rm -rf "$HOME/Library/Application Support/Box/Box" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Box/Box" ]; then
    rm -f "$HOME/Library/Application Support/Box/Box" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/FileProvider/com.box.desktop.boxfileprovider
echo "Removing $HOME/Library/Application Support/FileProvider/com.box.desktop.boxfileprovider..."
if [ -d "$HOME/Library/Application Support/FileProvider/com.box.desktop.boxfileprovider" ]; then
    rm -rf "$HOME/Library/Application Support/FileProvider/com.box.desktop.boxfileprovider" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/FileProvider/com.box.desktop.boxfileprovider" ]; then
    rm -f "$HOME/Library/Application Support/FileProvider/com.box.desktop.boxfileprovider" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.box.desktop.findersyncext
echo "Removing $HOME/Library/Containers/com.box.desktop.findersyncext..."
if [ -d "$HOME/Library/Containers/com.box.desktop.findersyncext" ]; then
    rm -rf "$HOME/Library/Containers/com.box.desktop.findersyncext" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.box.desktop.findersyncext" ]; then
    rm -f "$HOME/Library/Containers/com.box.desktop.findersyncext" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Box/Box
echo "Removing $HOME/Library/Logs/Box/Box..."
if [ -d "$HOME/Library/Logs/Box/Box" ]; then
    rm -rf "$HOME/Library/Logs/Box/Box" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Box/Box" ]; then
    rm -f "$HOME/Library/Logs/Box/Box" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.box.desktop.plist
echo "Removing $HOME/Library/Preferences/com.box.desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.box.desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.box.desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.box.desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.box.desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.box.desktop.ui.plist
echo "Removing $HOME/Library/Preferences/com.box.desktop.ui.plist..."
if [ -d "$HOME/Library/Preferences/com.box.desktop.ui.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.box.desktop.ui.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.box.desktop.ui.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.box.desktop.ui.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
