#!/bin/bash
# Uninstall script for Bunch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Bunch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Bunch if running..."
pkill -f "Bunch" 2>/dev/null || true

# Remove /Applications/Bunch.app
echo "Removing /Applications/Bunch.app..."
if [ -d "/Applications/Bunch.app" ]; then
    rm -rf "/Applications/Bunch.app" 2>/dev/null || true
elif [ -f "/Applications/Bunch.app" ]; then
    rm -f "/Applications/Bunch.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.brettterpstra.Bunch.plist
echo "Removing $HOME/Library/Preferences/com.brettterpstra.Bunch.plist..."
if [ -d "$HOME/Library/Preferences/com.brettterpstra.Bunch.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.brettterpstra.Bunch.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.brettterpstra.Bunch.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.brettterpstra.Bunch.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
