#!/bin/bash
# Uninstall script for Buttercup
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Buttercup..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Buttercup if running..."
pkill -f "Buttercup" 2>/dev/null || true

# Remove /Applications/Buttercup.app
echo "Removing /Applications/Buttercup.app..."
if [ -d "/Applications/Buttercup.app" ]; then
    rm -rf "/Applications/Buttercup.app" 2>/dev/null || true
elif [ -f "/Applications/Buttercup.app" ]; then
    rm -f "/Applications/Buttercup.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Buttercup
echo "Removing $HOME/Library/Application Support/Buttercup..."
if [ -d "$HOME/Library/Application Support/Buttercup" ]; then
    rm -rf "$HOME/Library/Application Support/Buttercup" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Buttercup" ]; then
    rm -f "$HOME/Library/Application Support/Buttercup" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Buttercup-nodejs
echo "Removing $HOME/Library/Application Support/Buttercup-nodejs..."
if [ -d "$HOME/Library/Application Support/Buttercup-nodejs" ]; then
    rm -rf "$HOME/Library/Application Support/Buttercup-nodejs" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Buttercup-nodejs" ]; then
    rm -f "$HOME/Library/Application Support/Buttercup-nodejs" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Buttercup-nodejs
echo "Removing $HOME/Library/Logs/Buttercup-nodejs..."
if [ -d "$HOME/Library/Logs/Buttercup-nodejs" ]; then
    rm -rf "$HOME/Library/Logs/Buttercup-nodejs" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Buttercup-nodejs" ]; then
    rm -f "$HOME/Library/Logs/Buttercup-nodejs" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Buttercup-nodejs
echo "Removing $HOME/Library/Preferences/Buttercup-nodejs..."
if [ -d "$HOME/Library/Preferences/Buttercup-nodejs" ]; then
    rm -rf "$HOME/Library/Preferences/Buttercup-nodejs" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Buttercup-nodejs" ]; then
    rm -f "$HOME/Library/Preferences/Buttercup-nodejs" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/pw.buttercup.desktop.plist
echo "Removing $HOME/Library/Preferences/pw.buttercup.desktop.plist..."
if [ -d "$HOME/Library/Preferences/pw.buttercup.desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/pw.buttercup.desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/pw.buttercup.desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/pw.buttercup.desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/pw.buttercup.desktop.savedState
echo "Removing $HOME/Library/Saved Application State/pw.buttercup.desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/pw.buttercup.desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/pw.buttercup.desktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/pw.buttercup.desktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/pw.buttercup.desktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
