#!/bin/bash
# Uninstall script for Cacher
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Cacher..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Cacher if running..."
pkill -f "Cacher" 2>/dev/null || true

# Remove /Applications/Cacher.app
echo "Removing /Applications/Cacher.app..."
if [ -d "/Applications/Cacher.app" ]; then
    rm -rf "/Applications/Cacher.app" 2>/dev/null || true
elif [ -f "/Applications/Cacher.app" ]; then
    rm -f "/Applications/Cacher.app" 2>/dev/null || true
fi

# Remove $HOME/.cacher
echo "Removing $HOME/.cacher..."
if [ -d "$HOME/.cacher" ]; then
    rm -rf "$HOME/.cacher" 2>/dev/null || true
elif [ -f "$HOME/.cacher" ]; then
    rm -f "$HOME/.cacher" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Cacher
echo "Removing $HOME/Library/Application Support/Cacher..."
if [ -d "$HOME/Library/Application Support/Cacher" ]; then
    rm -rf "$HOME/Library/Application Support/Cacher" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Cacher" ]; then
    rm -f "$HOME/Library/Application Support/Cacher" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/net.PenguinLabs.Cacher.plist
echo "Removing $HOME/Library/Preferences/net.PenguinLabs.Cacher.plist..."
if [ -d "$HOME/Library/Preferences/net.PenguinLabs.Cacher.plist" ]; then
    rm -rf "$HOME/Library/Preferences/net.PenguinLabs.Cacher.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/net.PenguinLabs.Cacher.plist" ]; then
    rm -f "$HOME/Library/Preferences/net.PenguinLabs.Cacher.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/net.PenguinLabs.Cacher.savedState
echo "Removing $HOME/Library/Saved Application State/net.PenguinLabs.Cacher.savedState..."
if [ -d "$HOME/Library/Saved Application State/net.PenguinLabs.Cacher.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/net.PenguinLabs.Cacher.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/net.PenguinLabs.Cacher.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/net.PenguinLabs.Cacher.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
