#!/bin/bash
# Uninstall script for castr
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling castr..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping castr if running..."
pkill -f "castr" 2>/dev/null || true

# Remove /Applications/Castr.app
echo "Removing /Applications/Castr.app..."
if [ -d "/Applications/Castr.app" ]; then
    rm -rf "/Applications/Castr.app" 2>/dev/null || true
elif [ -f "/Applications/Castr.app" ]; then
    rm -f "/Applications/Castr.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Castr
echo "Removing $HOME/Library/Application Support/Castr..."
if [ -d "$HOME/Library/Application Support/Castr" ]; then
    rm -rf "$HOME/Library/Application Support/Castr" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Castr" ]; then
    rm -f "$HOME/Library/Application Support/Castr" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Castr
echo "Removing $HOME/Library/Logs/Castr..."
if [ -d "$HOME/Library/Logs/Castr" ]; then
    rm -rf "$HOME/Library/Logs/Castr" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Castr" ]; then
    rm -f "$HOME/Library/Logs/Castr" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
