#!/bin/bash
# Uninstall script for Catch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Catch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Catch if running..."
pkill -f "Catch" 2>/dev/null || true

# Remove /Applications/Catch.app
echo "Removing /Applications/Catch.app..."
if [ -d "/Applications/Catch.app" ]; then
    rm -rf "/Applications/Catch.app" 2>/dev/null || true
elif [ -f "/Applications/Catch.app" ]; then
    rm -f "/Applications/Catch.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.giorgiocalderolla.Catch.plist
echo "Removing $HOME/Library/Preferences/org.giorgiocalderolla.Catch.plist..."
if [ -d "$HOME/Library/Preferences/org.giorgiocalderolla.Catch.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.giorgiocalderolla.Catch.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.giorgiocalderolla.Catch.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.giorgiocalderolla.Catch.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
