#!/bin/bash
# Uninstall script for CheatSheet
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CheatSheet..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CheatSheet if running..."
pkill -f "CheatSheet" 2>/dev/null || true

# Remove /Applications/CheatSheet.app
echo "Removing /Applications/CheatSheet.app..."
if [ -d "/Applications/CheatSheet.app" ]; then
    rm -rf "/Applications/CheatSheet.app" 2>/dev/null || true
elif [ -f "/Applications/CheatSheet.app" ]; then
    rm -f "/Applications/CheatSheet.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.mediaatelier.CheatSheet
echo "Removing $HOME/Library/Application Support/com.mediaatelier.CheatSheet..."
if [ -d "$HOME/Library/Application Support/com.mediaatelier.CheatSheet" ]; then
    rm -rf "$HOME/Library/Application Support/com.mediaatelier.CheatSheet" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.mediaatelier.CheatSheet" ]; then
    rm -f "$HOME/Library/Application Support/com.mediaatelier.CheatSheet" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.mediaatelier.CheatSheet
echo "Removing $HOME/Library/Caches/com.mediaatelier.CheatSheet..."
