#!/bin/bash
# Uninstall script for CHIRP
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CHIRP..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CHIRP if running..."
pkill -f "CHIRP" 2>/dev/null || true

# Remove /Applications/CHIRP.app
echo "Removing /Applications/CHIRP.app..."
if [ -d "/Applications/CHIRP.app" ]; then
    rm -rf "/Applications/CHIRP.app" 2>/dev/null || true
elif [ -f "/Applications/CHIRP.app" ]; then
    rm -f "/Applications/CHIRP.app" 2>/dev/null || true
fi

# Remove $HOME/.chirp
echo "Removing $HOME/.chirp..."
if [ -d "$HOME/.chirp" ]; then
    rm -rf "$HOME/.chirp" 2>/dev/null || true
elif [ -f "$HOME/.chirp" ]; then
    rm -f "$HOME/.chirp" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
