#!/bin/bash
# Uninstall script for Claude Code
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Claude Code..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Claude Code if running..."
pkill -f "Claude Code" 2>/dev/null || true

# Remove /usr/local/bin/claude
echo "Removing /usr/local/bin/claude..."
if [ -d "/usr/local/bin/claude" ]; then
    rm -rf "/usr/local/bin/claude" 2>/dev/null || true
elif [ -f "/usr/local/bin/claude" ]; then
    rm -f "/usr/local/bin/claude" 2>/dev/null || true
fi

# Remove $HOME/.cache/claude
echo "Removing $HOME/.cache/claude..."
