    rm -rf "$HOME/.cache/claude" 2>/dev/null || true
elif [ -f "$HOME/.cache/claude" ]; then
    rm -f "$HOME/.cache/claude" 2>/dev/null || true
fi

# Remove $HOME/.claude.json*
echo "Removing $HOME/.claude.json*..."
if [ -d "$HOME/.claude.json*" ]; then
    rm -rf "$HOME/.claude.json*" 2>/dev/null || true
elif [ -f "$HOME/.claude.json*" ]; then
    rm -f "$HOME/.claude.json*" 2>/dev/null || true
fi

